(()=>{"use strict";(e=>{console.log(e);const t=document.querySelector("#timer-hours"),n=document.querySelector("#timer-minutes"),o=document.querySelector("#timer-seconds");document.querySelector(".timer-action");let r=0;r=setInterval((()=>{let i=(()=>{let t=(new Date(e).getTime()-(new Date).getTime())/1e3;return Math.floor(t/60/60/24),{timeRemaining:t,hours:Math.floor(t/60/60%24),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();t.textContent=("0"+i.hours).slice(-2),n.textContent=("0"+i.minutes).slice(-2),o.textContent=("0"+i.seconds).slice(-2),i.timeRemaining<0&&(clearInterval(r),t.textContent="00",n.textContent="00",o.textContent="00")}),1e3)})("17 february 2024")})();